mapper.update:
UPDATE students_courses_status
SET status_start_date = #{statusStartDate}, status_end_date = #{statusEndDate}, change_reason =
#{changeReason}
WHERE status_history_id = #{statusHistoryId}

UPDATE students_courses_status
SET status_end_date = DATE_ADD(CAST(#{statusStartDate} AS DATE), INTERVAL -1 DAY)
WHERE attending_id = #{attendingId}
AND status_end_date IS NULL;

UPDATE students_courses_status
SET status_end_date = DATEADD(DAY, -1, CAST(#{statusStartDate} AS DATE))
WHERE attending_id = #{attendingId}
AND status_end_date IS NULL;
